(7, 7, '_mail', 'a:9:{s:6:\"active\";b:1;s:7:\"subject\";s:31:\"[_site_title] \"Free inspection\"\";s:6:\"sender\";s:40:\"[_site_title] <wordpress@dwstaging.link>\";s:9:\"recipient\";s:19:\"[_site_admin_email]\";s:4:\"body\";s:197:\"From: [yourname][youremail]\n\nPhone:[yourphone]\nService type:\n[servicetype]\nCity:[yourcity]\n\n-- \nThis is a notification that a contact form was submitted on your website ([_site_title] [_site_url]).\";s:18:\"additional_headers\";s:0:\"\";s:11:\"attachments\";s:0:\"\";s:8:\"use_html\";b:0;s:13:\"exclude_blank\";b:0;}'),
(8, 7, '_mail_2', 'a:9:{s:6:\"active\";b:0;s:7:\"subject\";s:30:\"[_site_title] \"[your-subject]\"\";s:6:\"sender\";s:52:\"[_site_title] <pushan.chakraborty@webart.technology>\";s:9:\"recipient\";s:12:\"[your-email]\";s:4:\"body\";s:220:\"Message Body:\n[your-message]\n\n-- \nThis email is a receipt for your contact form submission on our website ([_site_title] [_site_url]) in which your email address was used. If that was not you, please ignore this message.\";s:18:\"additional_headers\";s:29:\"Reply-To: [_site_admin_email]\";s:11:\"attachments\";s:0:\"\";s:8:\"use_html\";b:0;s:13:\"exclude_blank\";b:0;}'),
